mapper.update:
update tb_admin_user
set user_token = #{newToken}
where id = #{userId}
  and is_deleted = 0

update tb_admin_user
set is_deleted=1 where id in

    #{id}

mapper.select:
select id, user_name, user_token
from tb_admin_user
where user_name = #{userName}
  and password_MD5 = #{passwordMD5}
  and is_deleted = 0
order by id desc limit 1
